import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Auth from './components/Auth';
import Counter from './components/Counter';
import Header from './components/Header';
import UserProfile from './components/UserProfile';
import { authaction, counteraction } from './store';


function App() {
  let check = useSelector(state => state.auth.IsAuthenticated)
  let dispatch = useDispatch()
  let islogin = () => dispatch(authaction.login())
  let islogout = () => dispatch(authaction.logout())
  // console.log(islogin);/
  console.log(check);


  return (
    <React.Fragment>
      {
        check && <Header istrue={islogin}></Header>
      }

      {

        !check && <Auth notrue={islogout}></Auth>
      }
      {/* <UserProfile></UserProfile> */}
      <Counter />

    </React.Fragment>
  );
}

export default App;
