import { createStore } from 'redux'
let statedata =
{
    counter: 0,
    isshown: true
}
let Reducerfunction = (state = statedata, action) => {
    if (action.type == 'increment') {
        return { counter: state.counter + 1, isshown: state.isshown }

    }
    if (action.type == 'decrement') {
        return {
            counter: state.counter - 1, isshown: state.isshown
        }
    }
    if (action.type == 'custom')
        return {
            counter: state.counter + action.payload, isshown: state.isshown
        }
    if (action.type == 'toggle') {
        return { isshown: !state.isshown, counter: state.counter }
    }


    return {
        counter: state.counter
    }
}
let store = createStore(Reducerfunction)
